import React, { useEffect } from "react";
import { withRouter } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import { connect, useSelector } from "react-redux";

//import BoardComponent from "../components/BoardComponent";
import Board from 'react-trello'



import { register } from "../redux/actions";

/**
 * For register new users
 * @param {props} props
 */

 const data = {
    lanes: [
      {
        id: 'lane1',
        title: 'To Do',
       // label: '2/2',
        cards: [
          {id: 'Card1', title: 'Assignment 1', description: 'Deadline: Sunday, May 2nd, 2021, 23:59. Assignee: None.                Priority:❗', label: '3 Storypoints', draggable: true},
          {id: 'Card2', title: 'Assignment 2', description: 'Deadline: Sunday, May 16th, 2021, 23:59. Assignee: None.                Priority:❗', label: '5 Storypoints', metadata: {sha: 'be312a1'}},
          {id: 'Card3', title: 'Assignment 3', description: 'Deadline: Sunday, July 25th, 2021, 23:59. Assignee: None.                 Priority:❗❗', label: '8 Storypoints', metadata: {sha: 'be312a1'}},
          {id: 'Card4', title: 'Final Exam', description: 'Deadline: Friday, Augusr 6th, 2021.     Assignee: None.                Priority:❗❗', label: '8 Storypoints', metadata: {sha: 'be312a1'}}
        ]
      },
      {
        id: 'lane2',
        title: 'In Review',
        //label: '0/0',
        cards: []
      },
      {
        id: 'lane3',
        title: 'In Progress',
       // label: '0/0',
        cards: []
      },
      {
        id: 'lane4',
        title: 'Done',
        //label: '0/0',
        cards: []
      }
    ]
  }

function BoardView(props) {
    const user = useSelector((state) => state.user);


    useEffect(() => {
        if (user.user) {
            props.history.push("/board");
        }
    }, [user, props.history]);

    return (
      <Board data={data} editable/>
    );




}

export default connect()(withRouter(BoardView));
