import React from 'react'
import { makeStyles } from "@material-ui/core/styles";


const useStyles = makeStyles((theme) => ({

	content: {
		gridArea: "content",
	},

	header: {
		gridArea: "header",
	},

    wrapper: {
        display: "grid",
    	gridGap: "300px",
       // gridTemplateAreas: "a a ." "a a .",
		gridTemplateColumns: "100px 100px 100px 100px",
       // marginBottom: "20px",
        background: "grey",
        width: "1500px",
        height: "1000px",
        marginLeft: "auto",
        marginRight: "auto",
        textAlign: "center",
        borderRadius: 15,
        verticalAlign: "center",
        padding: "10px",


	},

	box: {
        marginBottom: "20px",
        background: "white",
        width: "250px",
        height: "750px",
        marginLeft: "auto",
        marginRight: "auto",
        textAlign: "center",
        borderRadius: 15,
        verticalAlign: "center",
        padding: "10px",
        color: "black",

	}
}));
// <div className={classes.wrapper} >
function BoardComponent() {
    const classes = useStyles();
    return (
        <div className={classes.wrapper} style = "grid-template-areas: 'header header header header' 'content content content content' ">
        <div className={classes.box.header}>Task Tracker</div>
		<div className={classes.box.content}>To Do</div>
        <div className={classes.box.content}>In Progress</div>
        <div className={classes.box.content}>In Review</div>
        <div className={classes.box.content}>Done</div>

	</div>
    )
}

export default BoardComponent
