import React from "react";
import { withRouter } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import { Avatar, AppBar, Button, IconButton, TextField, Toolbar, Typography } from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";

import { connect, useSelector } from "react-redux";

import KebabMenu from "./KebabMenu";

import logo from './logo.png'; 

const useStyles = makeStyles((theme) => ({
    toolbar: {
        flexGrow: 1,
    },
    title: {
        flexGrow: 1,
        paddingLeft: theme.spacing(1),
    },
    logo: {
        maxHeight: "3.5rem",
    },
    searchBar: {
        flexGrow: 1,
        background: "#FFFFFF",
        margin: "0 20px 0 20px",
        borderRadius: 5,
        borderColor: "none"
    },
    projectName: {

    },
    signinButton: {
        margin: "5px",
        border: "2px",
        borderRadius: 5,
        borderColor: "inherit",
        color: "white",
        cursor: "pointer",
        '&:hover': {
            color: "black",
            background: "white",
            transition: "0.25s",
        }
    },
    signin: {
        margin: "10px",
        fontWeight: "normal",
    },
    signupButton: {
        background: "white",
        color: "black",
        cursor: "pointer",
        minWidth: 1,
        border: "1px solid white",
        borderRadius: 5, 
        '&:hover': {
            color: "white",
            background: "inherit",
            transition: "0.25s",
        }
    },
    signup: {
        margin: "10px",
        fontWeight: "normal",
    },
    avatar: {
        margin: "0 0 0 20px"
    }
}));

/**
 * Navigation bar of the app
 * @param {props} props
 */
function Header(props) {
    const classes = useStyles();

    const user = useSelector((state) => state.user);
    const project = useSelector((state) => state.currentProject);

    const [menuAnchor, setMenuAnchor] = React.useState(null);

    const searchbarDefaultText = "Search tasks, comments, people, ...";

    const onClickSignIn = () => {
        props.history.push("/login");
    }

    const onClickSignUp = () => {
        props.history.push("/register");
    }

    return (
        <AppBar position="sticky">
            <KebabMenu
                open={Boolean(menuAnchor)}
                anchor={menuAnchor}
                onClose={() => setMenuAnchor(null)}
            />
            <Toolbar className={classes.toolbar}>

                <img 
                    src={logo} 
                    className={classes.logo}
                    onClick={() => props.history.push("/")}
                />

                { /*
                Title will probably not be shown, since we have a logo already that includes the project name
                or show the logo without title and uncomment this

                <Typography
                    className={classes.title}
                    variant="h5"
                    color="inherit"
                >
                    Shrum
                </Typography>
                */ }

                <TextField 
                    className={classes.searchBar}
                    variant={"outlined"}
                    placeholder={searchbarDefaultText}
                />  

                { /* Legacy Movie App Code

                <IconButton onClick={onClickGithub} color="inherit">
                    <GitHubIcon />
                </IconButton>
                <IconButton onClick={props.toggletheme} color="inherit">
                    {props.darkmode ? <WbSunnyIcon /> : <Brightness3Icon />}
                </IconButton>
                <IconButton

                    color="inherit"
                >
                    <MenuIcon />
                </IconButton>

                // Pls dont delete: Alternative Login/Signup buttons
                        <div className={classes.signinButton}
                            onClick={onClickSignIn}>
                            <Typography
                                className={classes.signin}
                                variant="h5"
                                color="inherit"
                            >
                                Sign In
                            </Typography>
                        </div>

                        <div className={classes.signupButton}
                            onClick={onClickSignUp}>
                            <Typography
                                className={classes.signup}
                                variant="h5"
                                color="inherit"
                            >
                                Sign Up
                            </Typography>
                        </div>
                */
                }

                { typeof(user.user) === "undefined" ? (
                    <>

                        <Button 
                            className={classes.signinButton}
                            onClick={onClickSignIn}
                        >
                            Sign In
                        </Button>
                        <Button
                            className={classes.signupButton}
                            onClick={onClickSignUp}
                        >
                            Sign Up
                        </Button>
                    </>
                ) : (
                    <>
                        <Typography
                            className={classes.projectName}
                            variant="h5"
                            color="inherit"
                        >
                            { (typeof(project) === "undefined" || !project.project) ? "No project" : project.project.name }
                        </Typography>
                        <Avatar className={classes.avatar}
                            onClick={(event) => setMenuAnchor(event.currentTarget)}>
                            {user.user.username ? user.user.username[0] : ""}
                        </Avatar>
                    </>
                )}

            </Toolbar>
        </AppBar>
    );
}

export default connect()(withRouter(Header));
