import { makeStyles } from "@material-ui/core/styles";
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';


const useStyles = makeStyles((theme) => ({
    wrapper: {
        display: "grid",
    	gridGap: "100px",
       // gridTemplateAreas: "'header ....... ....... .......' 'content content content content",
        gridTemplateColumns: "100px  100px  100px  100px 100px",
        marginBottom: "20px",
        background: "white",
        width: "1500px",
        height: "1000px",
        marginLeft: "auto",
        marginRight: "auto",
        textAlign: "center",
        borderRadius: 15,
        verticalAlign: "center",
        padding: "10px",


	},

    //height: "750px",
	box: {
        marginTop: "100px",
        marginBottom: "20px",
        background: "white",
        width: "250px",
        height: "750px",
        marginLeft: "auto",
        marginRight: "auto",
        textAlign: "center",
        borderRadius: 15,
        verticalAlign: "center",
        padding: "10px",
        color: "black",
        background: "lightgray",

	},
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
}));
// <div className={classes.wrapper} >
//<div className={classes.header}>Task Tracker</div>
//<div className={classes.box}>To Do</div>
//<div className={classes.box}>In Progress</div>
//<div className={classes.box}>In Review</div>
//<div className={classes.box}>Done</div>

function BoardComponent() {
    const classes = useStyles();
    return (
        <div className={classes.wrapper}>
        <div className={"page-header"}>
            <h1 style={{ width: 500, marginLeft: 0 }}>Task Tracker</h1></div>
        <Grid container spacing={3}>
            <Grid item xs={3}>
                <Paper className={classes.paper}>To do</Paper>
            </Grid>
            <Grid item xs={3}>
                <Paper className={classes.paper}>in Review</Paper>
            </Grid>

        </Grid>
	</div>
    )
}

export default BoardComponent
