import React, { useEffect } from "react";
import { withRouter } from "react-router-dom";
import { connect, useSelector } from "react-redux";

import TaskCreationComponent from "../components/TaskCreationComponent";

import { createTask } from "../redux/actions";

/**
 * For register new users
 * @param {props} props
 */
function TaskCreationView(props) {
    const user = useSelector((state) => state.user);

    useEffect(() => {
        if (user.user) {
            props.history.push("/");
        }
    }, [user, props.history]);

    const onCreate = (project, taskData) => {
        props.dispatch(createTask(project, taskData));
    };

    const onSave = (project, taskData) => {
       // props.dispatch(saveTask(project, taskData));
    }

    const onCancel = () => {
        props.history.push("/");
    };

    return (
        <TaskCreationComponent
            user={user}
            onCreate={onCreate}
            onSave={onSave}
            onCancel={onCancel}
        />
    );
}

export default connect()(withRouter(TaskCreationView));
