import React, { useEffect } from "react";
import { withRouter } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import { connect, useSelector } from "react-redux";

//import BoardComponent from "../components/BoardComponent";
import Board from 'react-trello'



import { register } from "../redux/actions";

/**
 * For register new users
 * @param {props} props
 */

 const useStyles = makeStyles((theme) => ({

    //height: "750px",
	lanes: {
        marginTop: "100px",
        marginBottom: "20px",
        background: "white",
        width: "250px",
        height: "750px",
        marginLeft: "auto",
        marginRight: "auto",
        textAlign: "center",
        borderRadius: 15,
        verticalAlign: "center",
        padding: "10px",
        color: "black",
        background: "lightgray",

	}

}));


 const data = {
    lanes: [
      {
        id: 'lane1',
        title: 'To Do',
       // label: '2/2',
        cards: [
          {id: 'Card1', title: 'first Assignment', description: 'Deadline 01.05', label: '3 Storypoints', draggable: true},
          {id: 'Card2', title: 'Second Assignment', description: 'Deadline 01.07', label: '5 Storypoints', metadata: {sha: 'be312a1'}}
        ]
      },
      {
        id: 'lane2',
        title: 'In Review',
        //label: '0/0',
        cards: []
      },
      {
        id: 'lane3',
        title: 'In Progress',
       // label: '0/0',
        cards: []
      },
      {
        id: 'lane4',
        title: 'Done',
        //label: '0/0',
        cards: []
      }
    ]
  }

function BoardView(props) {
    const user = useSelector((state) => state.user);
    const classes = useStyles();

    useEffect(() => {
        if (user.user) {
            props.history.push("/board");
        }
    }, [user, props.history]);

    return (
      <Board data={data} />
    );




}

export default connect()(withRouter(BoardView));
